          decode32(texture(cc_jointTexture, vec2((x + 1.5) * invSize, y))),
          decode32(texture(cc_jointTexture, vec2((x + 2.5) * invSize, y))),
          decode32(texture(cc_jointTexture, vec2((x + 3.5) * invSize, y)))
