      fDeltaY = (_FogTop - wPos.y) / _FogRange * 2.0;
      fDensityIntegral = fDeltaY * fDeltaY * 0.5;
    } else {
      fDeltaY = 0.;
      fDensityIntegral = 0.;
    }
